    active = list(
        frequencies = function() private$.items[["frequencies"]],
        comp = function() private$.items[["comp"]],
        plot = function() private$.items[["plot"]]),
    private = list(),
    public=list(
        initialize=function(options) {
            super$initialize(
                options=options,
                name="",
