                        `title`="Questions", 
                        `type`="text")),
                clearWith=list(
                    "liks",
                    "group",
                    "type",
                    "frequencies",
                    "showMean",
                    "frequencyTable",
                    "toInteger",
                    "tidyUp",
                    "descAsVarName")))
            self$add(R6::R6Class(
                inherit = jmvcore::Group,
                active = list(
                    uTestTable = function() private$.items[["uTestTable"]],
                    kwTable = function() private$.items[["kwTable"]],
                    pwTable = function() private$.items[["pwTable"]]),
                private = list(),
                public=list(
                    initialize=function(options) {
                        super$initialize(
                            options=options,
                            name="comp",
                            title="Comparison Tests")
                        self$add(jmvcore::Table$new(
                            options=options,
                            name="uTestTable",
