                                "group",
                                "adjustMethod",
                                "pValue",
                                "descAsVarName")))
                        self$add(jmvcore::Table$new(
                            options=options,
                            name="kwTable",
                            title="Kruskal-Wallis Tests",
                            rows="(liks)",
                            visible="(showKW)",
                            columns=list(
                                list(
                                    `name`="ques", 
                                    `title`="Questions", 
                                    `type`="text", 
                                    `content`="($key)"),
                                list(
                                    `name`="statistic", 
                                    `title`="\u03C7\u00B2", 
                                    `type`="number"),
                                list(
                                    `name`="parameter", 
