                            clearWith=list(
                                "liks",
                                "group",
                                "adjustMethod",
                                "pValue",
                                "descAsVarName")))
                        self$add(jmvcore::Table$new(
                            options=options,
                            name="pwTable",
                            title="Pairwise Comparisons",
                            rows=0,
                            visible="(showPostHoc)",
                            columns=list(
