#'   \code{results$comp$kwTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$comp$pwTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$frequencies$asDF}
#'
#' \code{as.data.frame(results$frequencies)}
#'
#' @export
likertplot <- function(
    data,
    liks,
    group,
    type = "centered",
    sorting = "none",
    groupBy = "variable",
    addLabels = TRUE,
    hideLabelsBelow = FALSE,
    addTotals = TRUE,
    addMedianLine = TRUE,
    reverseLikert = FALSE,
    toInteger = FALSE,
    tidyUp = FALSE,
    ignoreNA = TRUE,
    textSize,
    labelSize = 11,
    groupSize = 12,
    accuracy = "1",
    vLabelWrap = "20",
    hLabelWrap = "30",
    plotColor = "BrBG",
    labelColor = "auto",
    frequencyTable = FALSE,
    showMedian = FALSE,
    showMean = FALSE,
    frequencies = "counts",
    showMannU = FALSE,
    showKW = FALSE,
    showPostHoc = FALSE,
    postHoc = "dunn",
    pValue = "none",
    adjustMethod = "holm",
    descAsVarName = FALSE,
    titleText,
    titleFontFace = "bold",
    titleFontSize = "14",
    titleAlign = "0.5",
    subtitleText,
    subtitleFontFace = "plain",
    subtitleFontSize = "12",
    subtitleAlign = "0.5",
    captionText,
    captionFontFace = "italic",
    captionFontSize = "10",
    captionAlign = "1",
    legendText,
    legendFontSize = "11",
    legendPosition = "bottom",
    yAxisLabelFontSize = 11,
    yAxisLabelRotation = 0,
    xAxisLabelFontSize = 11,
    xAxisLabelRotation = 0) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("likertplot requires jmvcore to be installed (restart may be required)")

    if ( ! missing(liks)) liks <- jmvcore::resolveQuo(jmvcore::enquo(liks))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(liks), liks, NULL),
            `if`( ! missing(group), group, NULL))

    for (v in group) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- likertplotOptions$new(
        liks = liks,
        group = group,
        type = type,
        sorting = sorting,
        groupBy = groupBy,
        addLabels = addLabels,
        hideLabelsBelow = hideLabelsBelow,
        addTotals = addTotals,
        addMedianLine = addMedianLine,
        reverseLikert = reverseLikert,
        toInteger = toInteger,
        tidyUp = tidyUp,
        ignoreNA = ignoreNA,
        textSize = textSize,
        labelSize = labelSize,
        groupSize = groupSize,
        accuracy = accuracy,
        vLabelWrap = vLabelWrap,
        hLabelWrap = hLabelWrap,
        plotColor = plotColor,
        labelColor = labelColor,
        frequencyTable = frequencyTable,
        showMedian = showMedian,
        showMean = showMean,
        frequencies = frequencies,
        showMannU = showMannU,
        showKW = showKW,
        showPostHoc = showPostHoc,
        postHoc = postHoc,
        pValue = pValue,
        adjustMethod = adjustMethod,
        descAsVarName = descAsVarName,
        titleText = titleText,
        titleFontFace = titleFontFace,
        titleFontSize = titleFontSize,
        titleAlign = titleAlign,
        subtitleText = subtitleText,
        subtitleFontFace = subtitleFontFace,
        subtitleFontSize = subtitleFontSize,
        subtitleAlign = subtitleAlign,
        captionText = captionText,
        captionFontFace = captionFontFace,
        captionFontSize = captionFontSize,
        captionAlign = captionAlign,
        legendText = legendText,
        legendFontSize = legendFontSize,
        legendPosition = legendPosition,
        yAxisLabelFontSize = yAxisLabelFontSize,
        yAxisLabelRotation = yAxisLabelRotation,
        xAxisLabelFontSize = xAxisLabelFontSize,
        xAxisLabelRotation = xAxisLabelRotation)

    analysis <- likertplotClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

